'use strict';

var slideout = new Slideout({
    'panel': document.getElementById('container'),
    'menu': document.getElementById('menu'),
    'fx': 'ease-in-out',
    'padding': 160,
    'tolerance': 70
});

var toggleMenu = document.querySelector('.toggle-menu');

toggleMenu.addEventListener('click', function (e) {
    slideout.toggle();
});

var liMenuList = [].slice.call(document.querySelectorAll('#menu ul li'));

liMenuList.forEach(function (li) {
    li.addEventListener('click', function (event) {
        smoothScroll.init({
            'selectorHeader': '#menu'
        });
        var none = getComputedStyle(toggleMenu).getPropertyValue('display') || toggleMenu.currentStyle.display;
        if (none !== 'none') {
            smoothScroll.init();
            slideout.toggle();
        }
    });
});